package codeForces_800;

import java.util.Scanner;

public class DominoPiling {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//50A
		Scanner sc=new Scanner(System.in);
		int m=sc.nextInt();
		int n=sc.nextInt();
		System.out.println((int)(m*n)/2);
		
		sc.close();

	}

}
